import { createStore } from "vuex";

const store = createStore({
  state() {
    return {
      name: "kim",
      age: 20,
      likes: 30,
      liked:false
    };
  },
  mutations: {
    nameChange(state) {
      state.name = "park";
    },
    addAge(state, payload) {
      state.age += payload;
    },
    like(state){
        if(liked){

            state.likes++;
        }
    }
  },
});

export default store;
